import { IntlProvider } from 'i18n'
import { Metadata } from 'next'
import getServerTranslations from 'src/utils/getServerTranslations'
import NewHome from 'ui/features/new-home'

type Params = { params: { locale: string } }

export async function generateMetadata({ params }: Params): Promise<Metadata> {
  const { t } = await getServerTranslations({ locale: params.locale })

  return {
    title: t('home-page.citytrips.header'),
    description: t('home-page.single.city.page.meta.data.description'),
    keywords: t('home-page.single.city.page.meta.data.keywords'),
  }
}

export default async function SingleCity({
  params,
}: Params): Promise<JSX.Element> {
  const { t } = await getServerTranslations({ locale: params.locale })

  const data = {
    title: t('home-page.citytrips.header'),
    subtitle: t('home-page.citytrips.subheader'),
    image: '/static/backgrounds/trypcom_singlecity.png',
    blurImage:
      'data:image/jpeg;base64,',
  }

  return (
    <IntlProvider page="/">
      <NewHome data={data} />
    </IntlProvider>
  )
}
